#!/bin/bash

while [[ $# -gt 0 ]]; do
  key="$1"
  echo "Key: $key"
  case $key in
    -s)
      SERVER_HOST="$2"
      shift
      ;;
    --server-host=*)
      SERVER_HOST="${key#*=}"
      ;;
      *)
      ;;
  esac
  shift
done

script_dir="$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)"

# this will make sure that an empty eMMC card will get the waggle image
# and try to recover uSD to make the uSD 3 partitions
echo "recover me" > /root/do_recovery

# this will trigger a self test on the first full boot
touch /home/waggle/start_test

# (re)build the /etc/hosts file
if [ ${SERVER_HOST}x != "x" ] ; then
  cp $script_dir/../etc/hosts /etc/hosts
  sed -i "s/SERVER_HOST/${SERVER_HOST}/" /etc/hosts
fi

# Restrict SSH connections to local port bindings and ethernet card subnet
sed -i 's/^#ListenAddress ::$/ListenAddress 127.0.0.1/' /etc/ssh/sshd_config
sed -i 's/^#ListenAddress 0.0.0.0$/ListenAddress 10.31.81.10/' /etc/ssh/sshd_config

# disable all password authentication
sed -i 's/^#PasswordAuthentication yes$/PasswordAuthentication no/' /etc/ssh/sshd_config



if [ $(lsb_release -r | tr "\t" " " | sed "s/\ //g"  | cut -d ":" -f 2) = "16.04" ]; then
  # NetworkManager will try to manage any interfaces *not* listed in
  # /etc/network/interfaces, so just replace it with what we want
  cp ./etc/network/interfaces /etc/network/interfaces
elif [ $(lsb_release -r | tr "\t" " " | sed "s/\ //g"  | cut -d ":" -f 2) = "18.04" ]; then
  # Ubuntu 18.04 uses netplan https://netplan.io/
  cp ./etc/netplan/50-waggle.yaml /etc/netplan/50-waggle.yaml
fi

rm -f /etc/sudoers.d/waggle*
cp ./etc/sudoers.d/* /etc/sudoers.d/

# add AoT test cert for waggle user
echo "command=\"/bin/date\" ssh-rsa  AoT_key_test" > /home/waggle/.ssh/authorized_keys
echo >> /home/waggle/.ssh/authorized_keys

# add AoT node controller cert for root
mkdir -p /root/.ssh/
chmod 744 /root/.ssh
echo "ssh-rsa  AoT_key" > /root/.ssh/authorized_keys
echo >> /root/.ssh/authorized_keys

# add AoT Edge Processor cert for root
#echo "from=\"10.31.81.5?\" ssh-rsa  AoT_edge_processor_key" >> /root/.ssh/authorized_keys
#echo >> /root/.ssh/authorized_keys

# add an ssh config host for the Edge Processor
cat <<EOT > /root/.ssh/config
ForwardAgent yes
Host beehive
    Hostname beehive
    StrictHostKeyChecking no
    BatchMode yes
    User root
    Port 20022
    IdentityFile /etc/waggle/key.pem
Host ep ep1
    Hostname edgeprocessor1
    StrictHostKeyChecking no
    BatchMode yes
    User root
    Port 22
    IdentityFile /usr/lib/waggle/SSL/edge_processor/id_rsa_waggle_edge_processor
EOT
 
# Setup RabbitMQ config files.
cp -r /usr/lib/waggle/nodecontroller/etc/rabbitmq /etc

# Updating path to nc scripts in /root/.bashrc
echo "PATH=$PATH:/usr/lib/waggle/nodecontroller/scripts/" >> /root/.bashrc


# Just in case for now...ideally this would be in /etc/envinronment already.
NODE_ID=$(ip link | awk '/ether 00:1e:06/ { print $2 }' | sed 's/://g')
sed -i -e "s/%NODE_ID%/$NODE_ID/" /etc/rabbitmq/rabbitmq.config
